package cn.redcdn.messagereminder;import android.content.Context;import android.util.AttributeSet;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.widget.FrameLayout;import cn.redcdn.util.MResource;public class MessageReminderView extends FrameLayout {  private final String TAG = MessageReminderView.this.getClass().getName();  private Context mContext;  public MessageReminderView(Context context) {    this(context, null);  }  public MessageReminderView(Context context, AttributeSet attrs) {    this(context, attrs, 0);  }  public MessageReminderView(Context context, AttributeSet attrs, int defStyle) {    super(context, attrs, defStyle);    Log.d(TAG, "MessageReminderView构造");    mContext = context;    LayoutInflater.from(context).inflate(MResource.getIdByName(context, MResource.LAYOUT, "meeting_room_msg"), this, true);    setVisibility(View.INVISIBLE);  }  public void release() {    setVisibility(View.INVISIBLE);  }}